import React, { useRef, useEffect, useState } from 'react';
import axios from 'axios';

const VideoStream = () => {
  const [detections, setDetections] = useState([]);
  const [animalCounts, setAnimalCounts] = useState({});
  const videoRef = useRef(null);

  useEffect(() => {
    const startVideo = async () => {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      videoRef.current.play();
    };

    startVideo();

    const captureFrame = async () => {
      if (videoRef.current) {
        const canvas = document.createElement('canvas');
        canvas.width = videoRef.current.videoWidth;
        canvas.height = videoRef.current.videoHeight;
        const ctx = canvas.getContext('2d');
        ctx.drawImage(videoRef.current, 0, 0);

        canvas.toBlob(async (blob) => {
          const formData = new FormData();
          formData.append('file', blob, 'frame.jpg');

          try {
            const response = await axios.post('http://localhost:5000/detect', formData, {
              headers: {
                'Content-Type': 'multipart/form-data'
              }
            });
            const detected = response.data.detections || [];
            setDetections(detected);

            // Count the number of each animal detected
            const counts = detected.reduce((acc, obj) => {
              acc[obj.name] = (acc[obj.name] || 0) + 1;
              return acc;
            }, {});
            setAnimalCounts(counts);
          } catch (error) {
            console.error('Error sending frame:', error);
          }
        }, 'image/jpeg');

        // Call captureFrame again to capture the next frame
        requestAnimationFrame(captureFrame);
      }
    };

    captureFrame();
  }, []);

  return (
    <div>
      <h1>Real-time Object Detection</h1>
      <video ref={videoRef} style={{ width: '100%' }}></video>
      <div>
        <h2>Detected Animals</h2>
        <ul>
          {detections.map((det, index) => (
            <li key={index}>{det.name} ({det.confidence.toFixed(2)})</li>
          ))}
        </ul>
      </div>
      <div>
        <h2>Animal Counts</h2>
        <table>
          <thead>
            <tr>
              <th>Animal</th>
              <th>Count</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(animalCounts).map(([animal, count], index) => (
              <tr key={index}>
                <td>{animal}</td>
                <td>{count}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default VideoStream;
