import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import './App.css';

function CameraAndTable() {
  const [animalCounts, setAnimalCounts] = useState({});
  const videoRef = useRef(null);

  useEffect(() => {
    // Start the camera feed
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (error) {
        console.error('Error accessing the camera:', error);
      }
    };

    startCamera();

    // Fetch animal counts every 5 seconds
    const fetchCounts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/animal_counts');
        setAnimalCounts(response.data);
      } catch (error) {
        console.error('Error fetching animal counts:', error);
      }
    };

    fetchCounts(); // Initial fetch
    const interval = setInterval(fetchCounts, 5001); // Update every 5 seconds

    return () => clearInterval(interval); // Cleanup on component unmount
  }, []);

  return (
    <div>
      <h1>Live Camera Feed and Object Detection</h1>
      <div>
        <video
          ref={videoRef}
          autoPlay
          style={{ width: '80%', height: 'auto', border: '1px solid black' }}
        />
      </div>
      <div id="counts" style={{ marginTop: '20px' }}>
        <h2>Animal Counts:</h2>
        <table>
          <thead>
            <tr>
              <th>Animal</th>
              <th>Count</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(animalCounts).map(([animal, count]) => (
              <tr key={animal}>
                <td>{animal}</td>
                <td>{count}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default CameraAndTable;
