import React, { useRef, useEffect, useState } from 'react';
import axios from 'axios';

const VideoStream = () => {
  const [detections, setDetections] = useState([]);
  const [animalCounts, setAnimalCounts] = useState({});
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    const startVideo = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        videoRef.current.srcObject = stream;
        videoRef.current.play();
      } catch (error) {
        console.error('Error accessing webcam:', error);
      }
    };

    startVideo();

    const captureFrame = async () => {
      if (videoRef.current && canvasRef.current) {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');

        canvas.width = 300;
        canvas.height = 340;

        try {
          ctx.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

          const blob = await new Promise((resolve) => canvas.toBlob(resolve, 'image/jpeg'));

          if (!blob) {
            console.error('Error creating blob from canvas');
            return;
          }

          const formData = new FormData();
          formData.append('file', blob, 'frame.jpg');

          try {
            const response = await axios.post('http://localhost:5000/detect', formData, {
              headers: {
                'Content-Type': 'multipart/form-data'
              }
            });

            if (response.data && response.data.detections) {
              const detected = response.data.detections;
              setDetections(detected);

              const counts = detected.reduce((acc, obj) => {
                acc[obj.name] = (acc[obj.name] || 0) + 1;
                return acc;
              }, {});
              setAnimalCounts(counts);
            }
          } catch (error) {
            console.error('Error sending frame:', error);
          }
        } catch (error) {
          console.error('Error processing canvas:', error);
        }

        requestAnimationFrame(captureFrame);
      }
    };

    captureFrame();
  }, []);

  return (
    <div>
      <h1>Real-time Object Detection</h1>
      <video ref={videoRef} style={{ width: '320px', height: '384px', display: 'block' }}></video>
      <canvas ref={canvasRef} style={{ display: 'none' }}></canvas>
      <div>
        <h2>Detected Animals</h2>
        <ul>
          {detections.map((det, index) => (
            <li key={index}>{det.name} ({det.confidence.toFixed(2)})</li>
          ))}
        </ul>
      </div>
      <div>
        <h2>Animal Counts</h2>
        <table>
          <thead>
            <tr>
              <th>Animal</th>
              <th>Count</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(animalCounts).map(([animal, count], index) => (
              <tr key={index}>
                <td>{animal}</td>
                <td>{count}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default VideoStream;
