import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './CameraAndTable.css';

function CameraAndTable() {
  const [animalCounts, setAnimalCounts] = useState({});
  const [imageData, setImageData] = useState(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (error) {
        console.error('Error accessing the camera:', error);
      }
    };

    startCamera();

    const captureFrame = async () => {
      if (videoRef.current && canvasRef.current) {
        const canvas = canvasRef.current;
        const context = canvas.getContext('2d');
        canvas.width = videoRef.current.videoWidth;
        canvas.height = videoRef.current.videoHeight;
        context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);
        const dataURL = canvas.toDataURL('image/jpeg');
        setImageData(dataURL);
      }
    };

    const sendFrame = async () => {
      if (imageData) {
        const response = await axios.post('http://localhost:5000/detect', { file: imageData }, {
          headers: {
            'Content-Type': 'application/json',
          },
        });
        setAnimalCounts(response.data);
      }
    };

    const interval = setInterval(() => {
      captureFrame();
      sendFrame();
    }, 5000); // Capture and send every 5 seconds

    return () => clearInterval(interval);
  }, [imageData]);

  return (
    <div className="container">
      <div className="camera-preview">
        <video ref={videoRef} autoPlay style={{ width: '100%', height: 'auto' }} />
        <canvas ref={canvasRef} style={{ display: 'none' }} />
      </div>
      <div className="animal-counts">
        <h2>Animal Counts:</h2>
        <table>
          <thead>
            <tr>
              <th>Animal</th>
              <th>Count</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(animalCounts).length === 0 ? (
              <tr>
                <td colSpan="2">No data available</td>
              </tr>
            ) : (
              Object.entries(animalCounts).map(([animal, count]) => (
                <tr key={animal}>
                  <td>{animal}</td>
                  <td>{count}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default CameraAndTable;
