import React, { useRef, useEffect, useState } from 'react';
import axios from 'axios';

const VideoStream = () => {
  const [detections, setDetections] = useState([]);
  const [animalCounts, setAnimalCounts] = useState({});
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    let isMounted = true; // to track if component is still mounted

    const startVideo = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (videoRef.current && isMounted) {
          videoRef.current.srcObject = stream;
          videoRef.current.onloadedmetadata = () => {
            videoRef.current.play().catch((error) => {
              console.error('Error playing video:', error);
            });
          };
        }
      } catch (error) {
        console.error('Error accessing webcam:', error);
      }
    };

    startVideo();

    const captureFrame = async () => {
      if (videoRef.current && canvasRef.current) {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');

        canvas.width = videoRef.current.videoWidth;
        canvas.height = videoRef.current.videoHeight;

        ctx.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

        const blob = await new Promise((resolve) => canvas.toBlob(resolve, 'image/jpeg'));

        if (!blob) {
          console.error('Error creating blob from canvas');
          return;
        }

        const formData = new FormData();
        formData.append('file', blob, 'frame.jpg');

        try {
          const response = await axios.post('http://localhost:5000/detect', formData, {
            headers: {
              'Content-Type': 'multipart/form-data',
            },
          });

          if (response.data && response.data.detections) {
            console.log('Detections:', response.data.detections);
            setDetections(response.data.detections);

            const counts = response.data.detections.reduce((acc, obj) => {
              acc[obj.name] = (acc[obj.name] || 0) + 1;
              return acc;
            }, {});
            setAnimalCounts(counts);
          } else {
            console.log('No detections or response format incorrect', response.data);
          }
        } catch (error) {
          console.error('Error sending frame:', error);
        }
      }
      requestAnimationFrame(captureFrame);
    };

    captureFrame();

    return () => {
      isMounted = false; // cleanup function to mark component as unmounted
    };
  }, []);

  return (
    <div>
      <h1>Real-time Object Detection</h1>
      <video ref={videoRef} style={{ width: '320px', height: '240px', display: 'block' }}></video>
      <canvas ref={canvasRef} style={{ display: 'none' }}></canvas>
      <div>
        <h2>Detected Animals</h2>
        <ul>
          {detections.map((det, index) => (
            <li key={index}>{det.name} ({(det.confidence * 100).toFixed(2)}%)</li>
          ))}
        </ul>
      </div>
      <div>
        <h2>Animal Counts</h2>
        <table>
          <thead>
            <tr>
              <th>Animal</th>
              <th>Count</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(animalCounts).map(([animal, count], index) => (
              <tr key={index}>
                <td>{animal}</td>
                <td>{count}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default VideoStream;
