import React, { useRef, useEffect, useState } from 'react';
import axios from 'axios';

const VideoStream = () => {
  const [detections, setDetections] = useState([]);
  const [animalCounts, setAnimalCounts] = useState({});
  const [base64Image, setBase64Image] = useState('');
  const canvasRef = useRef(null);

  useEffect(() => {
    const handleBase64ToBlob = (base64) => {
      const [header, data] = base64.split(',');
      const mime = header.match(/:(.*?);/)[1];
      const binary = atob(data);
      const array = [];
      for (let i = 0; i < binary.length; i++) {
        array.push(binary.charCodeAt(i));
      }
      return new Blob([new Uint8Array(array)], { type: mime });
    };

    const sendImageToServer = async (blob) => {
      const formData = new FormData();
      formData.append('file', blob, 'image.png');

      try {
        const response = await axios.post('http://localhost:5000/detect', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });

        if (response.data && response.data.detections) {
          const detected = response.data.detections;
          setDetections(detected);

          const counts = detected.reduce((acc, obj) => {
            acc[obj.name] = (acc[obj.name] || 0) + 1;
            return acc;
          }, {});
          setAnimalCounts(counts);
        }
      } catch (error) {
        console.error('Error sending image:', error);
      }
    };

    if (base64Image) {
      const blob = handleBase64ToBlob(base64Image);
      sendImageToServer(blob);
    }
  }, [base64Image]);

  return (
    <div>
      <h1>Base64 Image Detection</h1>
      {/* Replace the following with your base64 image source or handling */}
      <img src={`data:image/png;base64,${base64Image}`} alt="Base64" />
      <div>
        <h2>Detected Animals</h2>
        <ul>
          {detections.map((det, index) => (
            <li key={index}>{det.name} ({det.confidence.toFixed(2)})</li>
          ))}
        </ul>
      </div>
      <div>
        <h2>Animal Counts</h2>
        <table>
          <thead>
            <tr>
              <th>Animal</th>
              <th>Count</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(animalCounts).map(([animal, count], index) => (
              <tr key={index}>
                <td>{animal}</td>
                <td>{count}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default VideoStream;
